mapper.select:
select r.request_id, r.applicant_id, r.status, r.admin_id, r.decision_time, r.decision_remark, r.remark, r.create_time, r.update_time,
           ua.nick_name as applicant_name, ub.nick_name as admin_name
      from reservation_request r
      left join sys_user ua on ua.user_id = r.applicant_id
      left join sys_user ub on ub.user_id = r.admin_id

         and r.request_id=#{requestId}
         and r.applicant_id=#{applicantId}
         and r.status=#{status}
         and r.create_time >= #{params.beginCreateTime}
         and r.create_time <= #{params.endCreateTime}

    order by r.request_id desc

mapper.update:
update reservation_request
status=#{status}, admin_id=#{adminId}, decision_time=#{decisionTime}, decision_remark=#{decisionRemark}, remark=#{remark}, update_time=now()
where request_id=#{requestId}
