mapper.select:
select  from room_reservation

     and room_id=#{roomId}
     and admin_id=#{adminId}

order by start_time desc

mapper.update:
update room_reservation
room_id=#{roomId}, start_time=#{startTime}, end_time=#{endTime}, update_time=now()
where reserve_id=#{reserveId}

mapper.select:
select count(1) from room_reservation
 where room_id in
   #{rid}
   and start_time < #{endTime}
   and end_time > #{startTime}
    and reserve_id != #{excludeId}

select 'RESERVATION' as sourceType,
       null as requestId,
       reserve_id as reserveId,
       room_id as roomId,
       start_time as startTime,
       end_time as endTime,
       null as status
  from room_reservation
 where room_id = #{roomId}
   and start_time < #{endTime}
   and end_time > #{startTime}
    and reserve_id != #{excludeId}
 order by start_time
